mapper.select:
SELECT applicationDocumentNm, userNm, scholarshipNm, file_url, uploaded_at, 
       original_file_name, file_size, content_type
FROM applicationdocument
WHERE applicationDocumentNm = #{documentNm} 
      AND userNm = #{userNm} 
      AND scholarshipNm = #{scholarshipNm}

mapper.insert:
INSERT INTO applicationdocument (
    applicationDocumentNm, userNm, scholarshipNm, file_url, uploaded_at, 
    original_file_name, file_size, content_type
)
VALUES (
    #{applicationDocumentNm}, #{userNm}, #{scholarshipNm}, #{fileUrl}, #{uploadedAt}, 
    #{originalFileName}, #{fileSize}, #{contentType}
)

mapper.update:
UPDATE applicationdocument
SET file_url = #{fileUrl},
    original_file_name = #{originalFileName},
    file_size = #{fileSize},
    content_type = #{contentType}
WHERE applicationDocumentNm = #{applicationDocumentNm}
      AND userNm = #{userNm}
      AND scholarshipNm = #{scholarshipNm}

mapper.delete:
DELETE FROM applicationdocument
WHERE applicationDocumentNm = #{documentNm}
      AND userNm = #{userNm}
      AND scholarshipNm = #{scholarshipNm}

mapper.select:
SELECT COUNT(*)
FROM applicationdocument
WHERE userNm = #{userNm} AND scholarshipNm = #{scholarshipNm}

SELECT COUNT(*)
FROM applicationdocument
WHERE userNm = #{userNm}

SELECT COUNT(*)
FROM applicationdocument
WHERE scholarshipNm = #{scholarshipNm}
