mapper.select:
SELECT userNm, scholarshipNm, state, applied_at, reason
FROM application
WHERE userNm = #{userNm} AND scholarshipNm = #{scholarshipNm}

mapper.update:
UPDATE application
SET state = #{state},
    reason = #{reason}
WHERE userNm = #{userNm} AND scholarshipNm = #{scholarshipNm}

mapper.select:
SELECT COUNT(*)
FROM application
WHERE state = #{state}

SELECT COUNT(*)
FROM application
WHERE userNm = #{userNm}

SELECT COUNT(*)
FROM application
WHERE scholarshipNm = #{scholarshipNm}
