mapper.select:
SELECT user_nm, scholarship_nm, state, applied_at, reason, updated_at, reviewed_by
FROM application
WHERE user_nm = #{userNm} AND scholarship_nm = #{scholarshipNm}

mapper.update:
UPDATE application
SET state = #{state},
    reason = #{reason},
    updated_at = #{updatedAt},
    reviewed_by = #{reviewedBy}
WHERE user_nm = #{userNm} AND scholarship_nm = #{scholarshipNm}

mapper.select:
SELECT COUNT(*)
FROM application
WHERE state = #{state}

SELECT COUNT(*)
FROM application
WHERE user_nm = #{userNm}

SELECT COUNT(*)
FROM application
WHERE scholarship_nm = #{scholarshipNm}
