mapper.select:
SELECT dept_nm, college_nm, univ_nm, name
FROM Department
WHERE dept_nm = #{deptNm} AND college_nm = #{collegeNm} AND univ_nm = #{univNm}

mapper.update:
UPDATE Department
SET name = #{name}
WHERE dept_nm = #{deptNm} AND college_nm = #{collegeNm} AND univ_nm = #{univNm}

mapper.select:
SELECT COUNT(*) > 0
FROM Department
WHERE dept_nm = #{deptNm} AND college_nm = #{collegeNm} AND univ_nm = #{univNm}
